package practice.sec01;

public class Forfor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int x = 0; x <= 10; x++) {
			for (int y = 0; y <= 10; y++) {
				if (60 - ((4 * x) + (5 * y)) == 0) {
					System.out.println(x + "," + y);

				}
			}
		}

	}

}
